import logo from './logo.svg';
import './App.css';
import { useDispatch, useSelector } from 'react-redux';
import { getWeatherThunk } from './redux/reducer';

function App() {
  const dispatch = useDispatch();
  const data = useSelector((state) => state.reducer.currentWeather);

  const buttonHandler = async () => {
    await dispatch(getWeatherThunk('Moscow'));
  };
  const { description, temp } = data;
  return (
    <>
      <div className="App">Hello, {description}</div>
      <h2>{temp}</h2>
      <button onClick={buttonHandler}>Get</button>
    </>
  );
}

export default App;
