import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import Service from '../service/service';

const initialState = {
  currentWeather: {
    description: 'Chegem',
    temp: 10,
  },
};

export const getWeatherThunk = createAsyncThunk(
  'weather/getWeatherThunk',
  async (id) => {
    const response = Service.getWeatherData(id);
    console.log(response);
    return response.main;
  }
);

const weatherSlice = createSlice({
  name: 'weather',
  initialState,
  reducers: {
    getWeather(state, action) {
      state.currentWeather.description = action.payload.description;
      state.currentWeather.temp = action.payload.temp;
    },
  },
  extraReducers: (builder) => {
    builder.addCase(getWeatherThunk.fulfilled, (state, action) => {
      const temp = action.payload;
      console.log(action);
      state.currentWeather[temp] = temp;
    });
  },
});

export const { getWeather } = weatherSlice.actions;
export default weatherSlice.reducer;
