class Service {
  static async getWeatherData(id) {
    const response = await fetch(
      `http://api.openweathermap.org/data/2.5/weather?q=${id}&lang=ru&appid=a9ebdb68b0dbf40182f5638efcca3afd&units=metric`
    );
    try {
      return response.json();
    } catch {
      return 'Not found';
    }
  }

  static async getIcon(icon) {
    const response = await fetch(
      `http://openweathermap.org/img/wn/${icon}@2x.png`
    );
    try {
      return response.text();
    } catch {
      return 'Not found';
    }
  }
}

export default Service;
