import { ForecastHour } from './ForecatHour';
import './Forecast.css';

export const Forecast = ({ forecast, isModalOpen, name }) => {
  let isOpen = isModalOpen ? 'show' : '';
  const forecasts = forecast.map((item) => {
    return (
      <ForecastHour
        key={item.dt}
        temp={Math.floor(item.main.temp * 1) / 1}
        icon={item.weather[0].icon}
        month={item.dt_txt.slice(5, 7)}
        day={item.dt_txt.slice(8, 10)}
        hour={item.dt_txt.slice(11, 13) * 1}
      />
    );
  });

  console.log(forecasts);
  return (
    <div className={'modal ' + isOpen}>
      <div class="modal-dialog">
        <div class="modal-content">
          <div class="modal-header">Прогноз погоды в городе {name}</div>
          <div class="modal-body"> {forecasts}</div>
        </div>
      </div>
    </div>
  );
};
